import React, { useEffect, useState  } from 'react';
import axios from 'axios';
import { useKoszyk } from '../Koszyk/KoszykContext.js';

const Produkty = () => {
    const [produkty, setProdukty] = useState([]);
    const { dodajDoKoszyka } = useKoszyk();

    useEffect(() => {
        const fetchProdukty = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/produkty'); //komunikacja z serwerem
                setProdukty(response.data);
            } catch (error) {
                console.error('Wystąpił błąd podczas pobierania produktów', error);
            }
        };

        fetchProdukty(); //efekt ten uruchomi się tylko raz, kiedy komponent zostanie zamontowany do drzewa DOM
    }, []);

    return (
        <div>
            <h2>Lista Produktów</h2>
            <p>Test renderowania komponentu</p> {/* Dodany tekst */}
            <ul>
                {produkty.map(produkt => (
                    <li key={produkt.id}>
                        {produkt.nazwa} - {produkt.cena} zł
                        <button onClick={() => dodajDoKoszyka(produkt)}>Dodaj do koszyka</button>
                    </li>
                ))}
            </ul>
        </div>
    );

};

export default Produkty;
