import React, { useState, useContext } from 'react';
import axios from 'axios';
import { KoszykContext } from '../Koszyk/KoszykContext.js'; // Zakładając, że masz taki kontekst

const Platnosci = () => {
    const { koszyk, wyczyscKoszyk } = useContext(KoszykContext);
    const [danePlatnosci, setDanePlatnosci] = useState({
        numerKarty: '',
        dataWaznosci: '',
        cvv: ''
    });

    const handleChange = (e) => {
        setDanePlatnosci({ ...danePlatnosci, [e.target.name]: e.target.value });
    };

    const zrealizujPlatnosc = async (e) => {
        e.preventDefault();
        try {
            // Tutaj można dodać logikę przetwarzania płatności, np. wywołanie API
            const response = await axios.post('http://localhost:3001/api/platnosci', {
                ...danePlatnosci,
                produkty: koszyk // Przesłanie informacji o produktach z koszyka
            });
            console.log(response.data);
            alert('Płatność została zrealizowana pomyślnie.');
            wyczyscKoszyk(); // Wyczyszczenie koszyka po pomyślnej płatności
        } catch (error) {
            console.error('Wystąpił błąd podczas realizacji płatności', error);
            alert('Nie udało się zrealizować płatności.');
        }
    };

    return (
        <form onSubmit={zrealizujPlatnosc}>
            <h2>Płatność</h2>
            <div>
                <label htmlFor="numerKarty">Numer karty:</label>
                <input id="numerKarty" type="text" name="numerKarty" value={danePlatnosci.numerKarty} onChange={handleChange} required />
            </div>
            <div>
                <label htmlFor="dataWaznosci">Data ważności:</label>
                <input id="dataWaznosci" type="text" name="dataWaznosci" value={danePlatnosci.dataWaznosci} onChange={handleChange} required />
            </div>
            <div>
                <label htmlFor="cvv">CVV:</label>
                <input id="cvv" type="text" name="cvv" value={danePlatnosci.cvv} onChange={handleChange} required />
            </div>
            <button type="submit">Zapłać</button>
        </form>
    );
};

export default Platnosci;
